// src/pages/SignPage.js
import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";  // Redux 관련 hooks
import { setTokens } from "../redux/actions/authActions"; // 액션 임포트
import "../components/Layout/HeaderStyle.css";
import "./onBoarding-layout.css";
import Button from "../components/Button/Button";
import { tempLogin } from "../services/loginService"; // Google 로그인 서비스 호출
import { folderPost } from "../services/folderService";
const ImagePlaceholder = ({ width, height }) => {
  const placeholderStyle = {
    width: `${width}px`,
    height: `${height}px`,
  };

  return (
    <div className="image-placeholder" style={placeholderStyle}>
      이미지 자리 표시지
    </div>
  );
};

const SignPage = () => {
  const dispatch = useDispatch();  // dispatch 사용
  const { accessToken, refreshToken } = useSelector((state) => state);  // Redux 상태에서 토큰 가져오기

  const [loading, setLoading] = useState(false);  // 로딩 상태 관리
  const [error, setError] = useState(null);       // 에러 상태 관리
  const [message, setMessage] = useState('');     // 성공 메시지 상태 관리

  // 임시 로그인 함수 호출 핸들러
const handleGoogleLogin = async () => {
  setLoading(true); // 로딩 상태 시작
  setError(null);   // 에러 상태 초기화
  setMessage('');   // 이전 메시지 초기화

  try {
    const headers = {
      "email": encodeURIComponent("mg0454@gmail.com"),
      "name": encodeURIComponent("성명근")
    };
    const response = await tempLogin(headers);  // tempLogin 호출

    console.log('Google Login Response:', response);  // 응답을 제대로 확인

    // 'response' 객체에서 직접 accessToken, refreshToken을 추출
    if (response && response.accessToken && response.refreshToken) {
      dispatch(setTokens(response.accessToken, response.refreshToken)); // 토큰 저장
      setMessage('로그인 성공!');  // 성공 메시지 설정
    } else {
      setError('로그인 응답에 문제가 있습니다.');  // 응답이 없거나 이상할 때 처리
    }
  } catch (err) {
    setError('로그인 실패! 다시 시도해주세요.');  // 에러 처리
    console.error('Login Error:', err);
  } finally {
    setLoading(false);  // 로딩 상태 종료
  }
};


  const hanldeFolderPost = async () => {
    try {
      const headers = {
        "name": encodeURIComponent("아무개")
      };
      const response = await folderPost(headers);
      console.log('folder:', response);
    } catch (err) {
      console.log('폴더 추가 실패!');
    }
  }
  return (
    <div className="signpage">
      <header className="header">
        <img
          className="logo"
          src="example.png"
          alt="logo"
          onClick={() => window.location.reload()}
        />
      </header>
      <main className="sign-main-style">
        <div className="image-section">
          <ImagePlaceholder width={362} height={320} /> {/* 여기서 크기 설정 */}
        </div>
        <div className="text-section">
          <h1 className="sign-slogan">서비스 메인 슬로건 코멘트</h1>
          <p>서비스 서브 슬로건 및 기타 설명</p>
          <div className="sign-text-button-wrapper">
            <Button
              label="Google 계정으로 로그인"
              onClick={handleGoogleLogin} // Google 로그인 핸들러 연결
            />
            <Button
              label="폴더추가"
              onClick={hanldeFolderPost}
            />
          </div>
        </div>
      </main>
    </div>
  );
};

export default SignPage;
