import React, { useState } from "react";
import "../components/Layout/HeaderStyle.css";
import "./onBoarding-layout.css";
import Button from "../components/Button/Button";
import { getGoogleLoginUrl, tempLogin } from "../services/loginService"; // Google 로그인 서비스 호출

const ImagePlaceholder = ({ width, height }) => {
  const placeholderStyle = {
    width: `${width}px`,
    height: `${height}px`,
  };

  return (
    <div className="image-placeholder" style={placeholderStyle}>
      이미지 자리 표시지
    </div>
  );
};

const SignPage = () => {
  const [loading, setLoading] = useState(false);  // 로딩 상태 관리
  const [error, setError] = useState(null);       // 에러 상태 관리
  const [message, setMessage] = useState('');     // 성공 메시지 상태 관리
  const [token, setToken] = useState('');         // 사용자 토큰 상태 관리 (임시 로그인 후 저장)

  // 임시 로그인 함수 호출 핸들러
  const handleGoogleLogin = async () => {
    setLoading(true);  // 로딩 상태 시작
    setError(null);    // 이전 에러 상태 초기화
    setMessage('');    // 이전 메시지 초기화

    // 임시 로그인 API 호출
    try {
      const headers = {
        "email": encodeURIComponent("mg0454@gmail.com"),
        "name": encodeURIComponent("성명근")
      }; // 기본 헤더 (인증 헤더 등 추가 필요 시)
      const response = await tempLogin(headers);  // tempLogin 함수 호출
      console.log('Login Response:', response);  // API 응답 로그
      setToken(response.token);  // 응답에서 토큰을 가져와 상태에 저장 (예시로 `token` 사용)
      setMessage('로그인 성공!'); // 성공 메시지 설정
    } catch (err) {
      setError('로그인 실패! 다시 시도해주세요.');  // 에러 메시지 설정
    } finally {
      setLoading(false);  // 로딩 상태 종료
    }
  };

  return (
    <div className="signpage">
      <header className="header">
        <img
          className="logo"
          src="example.png"
          alt="logo"
          onClick={() => window.location.reload()}
        />
      </header>
      <main className="sign-main-style">
        <div className="image-section">
          <ImagePlaceholder width={362} height={320} /> {/* 여기서 크기 설정 */}
        </div>
        <div className="text-section">
          <h1 className="sign-slogan">서비스 메인 슬로건 코멘트</h1>
          <p>서비스 서브 슬로건 및 기타 설명</p>
          <div className="sign-text-button-wrapper">
            <Button
              label="Google 계정으로 로그인"
              onClick={handleGoogleLogin} // Google 로그인 핸들러 연결
            />
          </div>
        </div>
      </main>
    </div>
  );
};

export default SignPage;
