// src/utils/apiClient.js
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL; // 환경 변수에서 URL 가져오기

const apiClient = axios.create({
  baseURL: API_BASE_URL, // baseURL 설정
  timeout: 10000, // 요청 타임아웃 (선택 사항)
  headers: {
    "Content-Type": "application/json",
  },
});

// GET 요청을 위한 함수
export const get = async (url, params = {}, headers = {}) => {
  const response = await apiClient.get(url, { params, headers });
  return response.data;
};

// POST 요청을 위한 함수
export const post = async (url, data = {}, headers = {}) => {
  const response = await apiClient.post(url, data, { headers });
  return response.data;
};

// PUT 요청을 위한 함수
export const put = async (url, data = {}, headers = {}) => {
  const response = await apiClient.put(url, data, { headers });
  return response.data;
};

// DELETE 요청을 위한 함수 (del 함수 추가)
export const del = async (url, headers = {}) => {
  const response = await apiClient.delete(url, { headers });
  return response.data;
};

export default apiClient;
