import React, { useState, useEffect, useRef } from "react";
import { Link, useLocation } from "react-router-dom";
import TextField from "../Textfield/Textfield";
import Button from "../Button/Button";
import Dropdown from "../Dropdown/Dropdown";
import Modal from "../Modal/Modal"; // Modal Ïª¥Ìè¨ÎÑåÌä∏ import

import "./HeaderStyle.css";

const Header = ({ type, toggleMenu }) => {
  const location = useLocation();
  const alarmOptions = [
    { content: "3Ïùº ÌõÑ Ìú¥ÏßÄÌÜµÏóêÏÑú nÍ∞úÏùò ÎßÅÌÅ¨Îì§Ïù¥ ÏòÅÏõêÌûà ÎπõÏùÑ ÏûÉÍ≤å Îê©ÎãàÎã§." },
    { content: "Îòê Ïñ¥Îñ§ ÏïåÎûåÏù¥ ÏûàÏùÑÍπåÏöî" },
    { content: "ÏÉàÎ°úÏö¥ ÏóÖÎç∞Ïù¥Ìä∏Í∞Ä ÏûàÏäµÎãàÎã§." },
    { content: "ÏÉàÎ°úÏö¥ ÏóÖÎç∞Ïù¥Ìä∏Í∞Ä ÏûàÏäµÎãàÎã§." },
    { content: "ÏÉàÎ°úÏö¥ ÏóÖÎç∞Ïù¥Ìä∏Í∞Ä ÏûàÏäµÎãàÎã§." },
    { content: "ÏÉàÎ°úÏö¥ ÏóÖÎç∞Ïù¥Ìä∏Í∞Ä ÏûàÏäµÎãàÎã§." },
  ].map((item) => ({
    ...item,
    img: "example.png",
  }));
  const folderOpt = ["Ìè¥Îçî1", "Ìè¥Îçî2", "Ìè¥Îçî3"].map((item) => ({
    label: item,
    content: item,
  }));
  const tagsOpt = ["Documents", "Pictures", "Music", "ÌÉúÍ∑∏Ïñ¥Ï©åÍµ¨1"].map(
    (item) => ({
      label: item,
      content: item,
    })
  );

  const headerStyle =
    type === "ONBOARDING"
      ? { backgroundColor: "#F1F1F1", paddingTop: "1rem" }
      : {};
  const [isModalOpen, setIsModalOpen] = useState(false); // Î™®Îã¨ ÏÉÅÌÉú Ï∂îÍ∞Ä

  // Î™®Îã¨ Ïó¥Í∏∞ Ìï®Ïàò
  const openModal = () => setIsModalOpen(true);

  // Î™®Îã¨ Îã´Í∏∞ Ìï®Ïàò
  const closeModal = () => setIsModalOpen(false);

  // Í≤ÄÏÉâ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [searchValue, setSearchValue] = useState("");
  const [recentSearches, setRecentSearches] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);
  const [filteredSearches, setFilteredSearches] = useState([]);

  const searchRef = useRef(null); // Í≤ÄÏÉâ ÌïÑÎìú Î∞è ÎìúÎ°≠Îã§Ïö¥ÏùÑ Í∞êÏßÄÌïòÎäî Ref
  const inputRef = useRef(null); // ÏûÖÎ†• ÌïÑÎìú Ï∞∏Ï°∞

  // Ï¥àÍ∏∞ ÏòàÏãú Îç∞Ïù¥ÌÑ∞ ÏÑ§Ï†ï
  useEffect(() => {
    const exampleSearches = ["React", "JavaScript", "Frontend", "CSS", "HTML"];
    setRecentSearches(exampleSearches);
    setFilteredSearches(exampleSearches);
  }, []);

  // Ïô∏Î∂Ä ÌÅ¥Î¶≠ Í∞êÏßÄ
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setShowDropdown(false); // Ïô∏Î∂Ä ÌÅ¥Î¶≠ Ïãú ÎìúÎ°≠Îã§Ïö¥ Ïà®Í∏∞Í∏∞
      }
    };

    document.addEventListener("click", handleClickOutside);

    return () => {
      document.removeEventListener("click", handleClickOutside); // Ïù¥Î≤§Ìä∏ Ï†ïÎ¶¨
    };
  }, []);

  // ÏûÖÎ†• ÌïÑÎìú ÌÅ¥Î¶≠ Ïãú ÎìúÎ°≠Îã§Ïö¥ ÌëúÏãú
  const handleTextClick = () => {
    setShowDropdown(true); // ÏûÖÎ†• ÌïÑÎìú ÌÅ¥Î¶≠ Ïãú ÎìúÎ°≠Îã§Ïö¥ ÌëúÏãú
  };

  // Í≤ÄÏÉâÏñ¥ ÏûÖÎ†• Ïãú Ï≤òÎ¶¨
  const handleSearchChange = (event) => {
    const inputValue = event.target.value;
    setSearchValue(inputValue);

    const filtered = recentSearches.filter((item) =>
      item.toLowerCase().includes(inputValue.toLowerCase())
    );

    setFilteredSearches(filtered);
    setShowDropdown(true);
  };

  // Í≤ÄÏÉâÏñ¥ ÏÑ†ÌÉù Ïãú Ï≤òÎ¶¨
  const handleSearchSelect = (search) => {
    setSearchValue(search); // ÏÑ†ÌÉùÌïú Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†• ÌïÑÎìúÏóê Î∞òÏòÅ
    setShowDropdown(false); // ÎìúÎ°≠Îã§Ïö¥ Ïà®ÍπÄ

    // Ïª§ÏÑúÎ•º Í≤ÄÏÉâÏñ¥ ÎÅùÏúºÎ°ú Ïù¥Îèô
    setTimeout(() => {
      if (inputRef.current) {
        const inputElement = inputRef.current;
        inputElement.setSelectionRange(search.length, search.length);
        inputElement.focus(); // Ìè¨Ïª§Ïä§ Ïú†ÏßÄ
      }
    }, 0);
  };  

  // ÏóîÌÑ∞ ÌÇ§ ÏûÖÎ†• Ïãú Í≤ÄÏÉâ Í∏∞Î°ù Ï∂îÍ∞Ä
  const handleAddSearch = (event) => {
    if (event.key === "Enter" && searchValue.trim()) {
      const updatedSearches = [
        searchValue,
        ...recentSearches.filter((item) => item !== searchValue), // Ï§ëÎ≥µ Ï†úÍ±∞
      ].slice(0, 5); // ÏµúÍ∑º Í≤ÄÏÉâÏñ¥ 5Í∞ú Ïú†ÏßÄ

      setRecentSearches(updatedSearches);
      setShowDropdown(false); // ÎìúÎ°≠Îã§Ïö¥ Ïà®ÍπÄ
      setSearchValue(""); // ÏûÖÎ†•Ï∞Ω Ï¥àÍ∏∞Ìôî
    }
  };

  return (
    <header className="header" style={headerStyle}>
      {/* ========== LINK CARD PAGES ========== */}
      {location.pathname !== "/" && (
        <>
          <Button className="menu" onClick={toggleMenu} label="üî≤" />
          <div className="searchers" ref={searchRef}> {/* Ref Ï∂îÍ∞Ä */}
          {/*classNameÏúºÎ°ú tagÎßêÍ≥† Îã§Î•∏ Í≤å Îì§Ïñ¥Í∞ÄÎ©¥ ÎèôÏûëÏù¥ Ïù¥ÏÉÅÌï¥ÏÑú ÏùºÎã® Ïù¥Í±∞ ÎÑ£Ïùå..*/}
            <Dropdown className="tag" options={folderOpt} label="Ìè¥Îçî" />{" "}
            <Dropdown className="tag" options={tagsOpt} label="ÌÉúÍ∑∏ÏÑ†ÌÉù" />
            <div className="search-input-container">
              <TextField
                ref={inputRef}
                value={searchValue}
                onChange={handleSearchChange}
                onClick={handleTextClick}
                onKeyDown={handleAddSearch}
                placeholder="Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî."
                className= "text-field"
              />
              {showDropdown && (
                <ul className="recent-search-dropdown">
                  {filteredSearches.map((search, index) => (
                    <li
                      key={index}
                      className="recent-search-item"
                      onClick={() => handleSearchSelect(search)}
                    >
                      {search}
                    </li>
                  ))}
                </ul>
              )}
            </div>
            <Button label="Í≤ÄÏÉâ" />
          </div>
          <div className="user_info">
            <Dropdown className="alarm" options={alarmOptions} label="ÏïåÎ¶º" />
            <Button label="Í≥ÑÏ†ïÏ†ïÎ≥¥" />
          </div>
        </>
      )}

      {/* ========== Landing PAGE ========== */}
      {location.pathname === "/" && (
        <>
          <img
            className="logo"
            src="example.png"
            alt="logo"
            onClick={() => window.location.reload()}
          />
          <div className="buttons">
            <Button label="ÌôïÏû• ÌîÑÎ°úÍ∑∏Îû® Ï∂îÍ∞ÄÌïòÍ∏∞" />
            <Link to="/homepage">
              <Button label="[ÏûÑÏãú]ÌôàP" />
            </Link>
            <Link to="/signpage">
              <Button label="[ÏûÑÏãú]SignP" />
            </Link>
            {/* Î™®Îã¨ Ïó¥Í∏∞ Î≤ÑÌäº */}
            <button className="header-btn" onClick={openModal}>
              [ÏûÑÏãú]Modal
            </button>
          </div>
        </>
      )}

      {/* Modal Ïª¥Ìè¨ÎÑåÌä∏ */}
      <Modal isOpen={isModalOpen} onClose={closeModal}>
        <h2 style={{ marginBottom: "1rem " }}>Example for Modal</h2>
        <p>Î™®Îã¨ ÎÇ¥Ïö©ÏûÖÎãàÎã§.</p>
        <p>Î™®</p>
        <p>Îã¨</p>
      </Modal>
    </header>
  );
};

export default Header;
